        request.get('/api/test')
            .then(res=>{
                setTest(res);
                console.log(tests[0]);
            })
            .catch(res=>{
                console.log(res.data);
            })
    }
    const Click = () => {  
        fetchData(); 
    }
    const render = tests.map((test, index) => 
        <li key={index}>{test.MASP} v√† {test.TENSP}</li>
    )
    return ( 
    <div>
        <h1>TEST</h1>
        <button onClick={Click}>get data</button>
        <div>
            <ul>{render}</ul> 
        </div>
    </div>
    )
}

export default Test;
