import images from "../../assets/images"; 
import './paymentResult.css'
import 'bootstrap/dist/css/bootstrap.css'; 
import * as request from "../../utils/request";
import { useEffect, useState } from "react";
import { Button } from "bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHeart } from "@fortawesome/free-regular-svg-icons";
import useGlobalVariableContext from "../../context_global_variable/context_global_variable";
import { faCheck } from "@fortawesome/free-solid-svg-icons";


function PaymentResult(){  

    const urlParams = new URLSearchParams(window.location.search);
    const [infoPaymentResult, setInfoPaymentResult] = useState([
         urlParams.get('vnp_Amount'),
         urlParams.get('vnp_BankCode'),
         urlParams.get('vnp_BankTranNo'),
         urlParams.get('vnp_OrderInfo'),
         urlParams.get('vnp_PayDate'),
         urlParams.get('vnp_ResponseCode'),
         urlParams.get('vnp_TxnRef'),
         urlParams.get('vnp_TransactionStatus'),
    ]); 

    const handlePaymentResult = () => {
        request.post('api/processPaymentResult', infoPaymentResult)
        .then(res => {
            console.log(res.data)
        })
    }

    useEffect(() => {  
        handlePaymentResult();
    }, []);  
    const renderInfoPaymentResult = infoPaymentResult.map(item,index => {
            return  <h6 key={index}>{item}</h6> 
    })
    return ( 
        <div class="container">  
            <div className="row">
                PaymentResult
            </div>  
        </div>
    )
}

export default PaymentResult;
