import { createContext, useContext, useState } from "react";

const LoginLogoutContext = createContext();

export function LoginLogoutProvider({children}){

    const [loginOrLogout, setLoginOrLogout] = useState("signIn");
    const [textQuery, setTextQuery] = useState("");
    const [resultQuery, setResultQuery] = useState(""); 
 
    return <LoginLogoutContext.Provider 
                value={{
                    loginOrLogout, setLoginOrLogout, 
                    textQuery, setTextQuery, 
                    resultQuery, setResultQuery, 
                }}
            >
        {children}
    </LoginLogoutContext.Provider>
}

export default function useLoginLogoutContext(){
    return useContext(LoginLogoutContext);
}
