import { useEffect, useRef, useState } from "react";
import images from "../../assets/images";
import './infoProduct.css';
import "bootstrap"
import { useParams } from "react-router-dom";
import request from "../../utils/request";
import useLoginLogoutContext from "../../context/toggleLoginLogout";

function InfoProduct(){

    // let { id } = useParams(); 
    const urlParams = new URLSearchParams(window.location.search);
    const id = urlParams.get('id'); 
    const {divPopupCartRef} = useLoginLogoutContext(); 
    const buttonAddToCartRef = useRef(null);

    const {setStatusPressAddToCart, statusPressAddToCart} = useLoginLogoutContext();
    const [soluong, setSoluong] = useState(1);
    const [infoProduct, setInfoProduct] = useState({});
    var dataAddProductToCart = ({
        matk: '',
        masp: '',
        soluongsp: 1,
        tonggia: 1, 
    });


    const handleInputSoLuong = (e) => {
        e.persist();

        setSoluong(parseInt(e.target.value))
        console.log(soluong)
    }

    const getInfo = () => { 
        
        request.get(`/api/infoProduct?id=${id}`)
        .then(res => { 
            // setInfoProduct(res.data[0]);
            console.log(res.data.data_sanpham);
        })
        .catch(e => {
            console.log(e);
        })
    }

    // xử lý khi hết hàng vẫn cố chấp thêm 
    const handleAddToCart =  (e) => {
        e.preventDefault();
    

        dataAddProductToCart = { 
            matk: localStorage.getItem("auth_matk"),
            masp: id,
            soluongsp: soluong,
            tonggia: soluong * infoProduct.GIABAN,
        }

        try{
            request.post("/api/addToCart", dataAddProductToCart)
            .then(res => {  
                setStatusPressAddToCart(statusPressAddToCart => !statusPressAddToCart);
            })
        }
        catch(err){ 
            console.log(err);
        }
         
        setTimeout(() => {
            setStatusPressAddToCart(false);
        }, 3000); // Thay đổi giá trị thời gian theo nhu cầu của bạn 
    }
    

    useEffect(() => {
        getInfo();
  

        const handleClickOutPopUpCart = (event) => {
            if(!buttonAddToCartRef.current.contains(event.target)){
                setStatusPressAddToCart(false); 
            }
        }

        const handleScroll = () => { 
            const scrollPosition = window.scrollY; 

            if (scrollPosition > 100) {
                setStatusPressAddToCart(false); 
            }  
        }
            
        window.addEventListener('scroll', handleScroll);
        document.addEventListener("click", handleClickOutPopUpCart);
    
        // Hủy đăng ký sự kiện khi component bị unmount
        return () => {
            window.removeEventListener('scroll', handleScroll);
            document.removeEventListener('click', handleClickOutPopUpCart);
        }; 
    } , []);
    return (
        <div class="container col-sm-12 ">
            <div class="container_info_product"> 
                <div class="product_image col-sm-7">
                    <div class="product_image__list_mini_image">
                        <div class="product_image__list_mini_image__item">
                            <img src={images.mini_image__item} alt="" class="product_image__list_mini_image__item__img"/>
                        </div> 
                        <div class="product_image__list_mini_image__item">
                            <img src={images.mini_image__item} alt="" class="product_image__list_mini_image__item__img"/>
                        </div>
                        <div class="product_image__list_mini_image__item">
                            <img src={images.mini_image__item} alt="" class="product_image__list_mini_image__item__img"/>
                        </div>
                        <div class="product_image__list_mini_image__item">
                            <img src={images.mini_image__item} alt="" class="product_image__list_mini_image__item__img"/>
                        </div>
                        <div class="product_image__list_mini_image__item">
                            <img src={images.mini_image__item} alt="" class="product_image__list_mini_image__item__img"/>
                        </div>
                    </div>
                    <div class="product_image__main_image">
                        <img src={images.mini_image__item} alt="" class="product_image__main_image__img"/>
                    </div>
                </div>
                <div class="detail_info_product col-sm-5">
                    <div class="detail_info_product__title">
                        <h4>{infoProduct.TENSP}</h4>
                    </div>
                    <div class="detail_info_product__price detail_info_product__price__css_chung">
                        <div class="detail_info_product__price__info_price">
                            <span class="detail_info_product__price__info_price__sell">{infoProduct.GIABAN}</span>
                            <span class="detail_info_product__price__info_price__origin">{infoProduct.GIAGOC}</span>
                            <span class="detail_info_product__price__info_price__decrease_percent">{parseInt((infoProduct.GIABAN / infoProduct.GIAGOC) * 100)}%</span> 
                        </div>
                        <div class="detail_info_product__price__review_quanlity">
                            <span>đánh giá số sao</span>
                        </div>  
                    </div>
                    <div class="detail_info_product__color detail_info_product__price__css_chung">
                        <div class="detail_info_product__color__text">
                            Màu sắc
                        </div>
                        <div class="detail_info_product__color__choose"> 
                            <div class="detail_info_product__color__item">
                                <input type="radio" class="check_color" id="color_1" name="color"/>
                                <label for="color_1" class="color_icon" id="color_1"></label>
                            </div>
                            <div class="detail_info_product__color__item">
                                <input type="radio" class="check_color" id="color_2" name="color"/>
                                <label for="color_2" class="color_icon" id="color_2"></label>
                            </div>
                            <div class="detail_info_product__color__item">
                                <input type="radio" class="check_color" id="color_3" name="color"/>
                                <label for="color_3" class="color_icon" id="color_3"></label>
                            </div>
                        </div>
                    </div>
                    <div class="detail_info_product__size detail_info_product__price__css_chung">
                        <div class="detail_info_product__size__text">
                            Kích thước
                        </div>
                        <div class="detail_info_product__size__choose"> 
                            <div class="detail_info_product__size__item">
                                <input type="radio" class="check_size" id="size_1" name="size"/>
                                <label for="size_1" class="size_icon" id="size_1">
                                    <span class="detail_info_product__size__item__text">S</span>
                                </label>
                            </div>
                            <div class="detail_info_product__size__item">
                                <input type="radio" class="check_size" id="size_2" name="size"/>
                                <label for="size_1" class="size_icon" id="size_2">
                                    <span class="detail_info_product__size__item__text">M</span>
                                </label>
                            </div>
                            <div class="detail_info_product__size__item">
                                <input type="radio" class="check_size" id="size_3" name="size"/>
                                <label for="size_1" class="size_icon" id="size_3">
                                    <span class="detail_info_product__size__item__text">L</span> 
                                </label>
                            </div>
                        </div>
                    </div>
                    <div class="detail_info_product__quantity detail_info_product__price__css_chung">
                        <div class="detail_info_product__quantity__div-small">
                            <input type="button" value="-" class="detail_info_product__quantity__adjust"/>
                            <input type="text" onChange={handleInputSoLuong} value={soluong} min={1} name="detail_info_product__quantity__number" class="detail_info_product__quantity__input_text"/>
                            <input type="button" value="+" class="detail_info_product__quantity__adjust"/>
                        </div>
                    </div>
                    <div ref={buttonAddToCartRef} class="detail_info_product__thanhtoan detail_info_product__price__css_chung">
                        <button  class="detail_info_product__thanhtoan__button" onClick={handleAddToCart}>
                            Thêm vào giỏ
                        </button>
                    </div>
                </div>
            </div>
        </div> 
    )
}

export default InfoProduct;
